vytvorObjednavky();
write("Začiatok spracovania objednávok");
for each objednavka in objednavky
	write("Kontrola dostupnosti objednávky");
write("Všetky objednávky spracované");
	if (objednavka.naSklade == TRUE)
		write("Objednávka na sklade, pokračujeme");
		objednavka.spracovatPlatbu();
		objednavka.odoslatObjednavku();
	else
		write("Objednávka nie je na sklade, preskakujem");
	end if;
end for;
write("Všetky objednávky spracované");

create list objednavky of Objednavka;
create object instance objednavka1 of Objednavka;
objednavka1.naSklade = TRUE;
add objednavka1 to objednavky;
create object instance objednavka2 of Objednavka;
objednavka2.naSklade = FALSE;
add objednavka2 to objednavky;
create object instance objednavka3 of Objednavka;
objednavka3.naSklade = TRUE;
add objednavka3 to objednavky;

write("Spracovanie platby");
jeUhradena = TRUE;
return jeUhradena;
